package com.tengfei.fairy.config;import android.app.Application;import android.content.Context;import java.io.File;/** * @author： * @date：2021/1/25-2:46 PM * @desc: 文件配置类 */public class FileConfig {    private static FileConfig mInstance;    private static Context mContext;    private FileConfig(){};    /**     * 初始化配置类     * @param mApp     */    public void initFileConf(Application mApp){        mContext = mApp;        if(mContext == null)            throw new NullPointerException("mContext is Null");    }    public static FileConfig getInstance(){        if(mInstance ==null)            synchronized (FileConfig.class){                if(mInstance == null)                    mInstance = new FileConfig();            }        return mInstance;    }    /**     * 缓存路径     * @return     */    public String getCachePath(){        return mContext.getExternalCacheDir().getPath();    }    /**     * 图片路径     * @return     */    public String getImgPath(){        File file = new File(mContext.getExternalCacheDir().getPath()+File.separator+"Image");        if(!file.exists())            file.mkdirs();        return file.getAbsolutePath();    }    /**     * 主题路径     * @return     */    public String getThemePath(){        File file = new File(mContext.getExternalFilesDir(null).getPath()+File.separator+"Theme");        if(!file.exists())            file.mkdirs();        return file.getAbsolutePath();    }    /**     * 获取Crash文件路径     * @return     */    public String getCrashFilePath(){        File file = new File(mContext.getExternalCacheDir().getPath()+File.separator+"Crash");        if(!file.exists())            file.mkdirs();        return file.getAbsolutePath();    }    /**     * 获取下载文件路径     * @return     */    public String getDownloadFilePath(){        File file = new File(mContext.getExternalCacheDir().getPath()+File.separator+"Download");        if(!file.exists())            file.mkdirs();        return file.getAbsolutePath();    }    /**     * 获取声纹目录     * @return     */    public String getRecordAudioFilePath(){        File file = new File(mContext.getExternalCacheDir().getPath()+File.separator+"recordAudio");        if(!file.exists())            file.mkdirs();        return file.getAbsolutePath();    }}